function params = displayParams% For UMC Utrecht% This assumes non-mirror mode:%   Windows laptop to  Dell 2007f LCDparams.numPixels    = [1600 1200];   params.dimensions   = [40.8 30.6];  % based on diagonal size of 51 cmparams.distance     = 75;           params.frameRate    = 60;           params.cmapDepth    = 8;            params.screenNumber = 1;            % % This assumes mirror mode:% % If mirroring, we change the number of pixels to match the Windows laptop,% % and we rescale the height because of cropping on the external display to% % match the aspect ratio of the laptop   % params.numPixels    = [1920 1080];   % params.dimensions   = [40.8 22.95];  % width remains the same, but height will be cropped with black bars% params.distance     = 75;           % params.frameRate    = 60;           % params.cmapDepth    = 8;            % params.screenNumber = 0;         % Descriptive parametersparams.position = 'UMC_EMU monitor';