function dispStringInCenter(params,dispString,vLoc,textSize)% dispStringInCenter(display,dispString,[vLoc],[textSize]);%% 06/2005 SOD ported to OSXif notDefined('vLoc'), vLoc = 0.5; endif notDefined('textSize'),	textSize = 20; enddisplay = params.display;w = display.windowPtr;woff=Screen(display.screenNumber, 'OpenOffscreenWindow');Screen('TextSize', w, textSize);Screen('TextSize', woff, textSize);bounds=TextBounds(woff,dispString);x = (display.rect(3)-bounds(3))/2;y = display.rect(4)*vLoc;Screen('DrawText', w, dispString, x, y, display.textColorRgb);drawFixation(params);Screen('Flip',w);return